mapper.select:
SELECT count(*)
FROM board_member
WHERE mid= #{mid}

SELECT mpassword
FROM board_member
WHERE mid = #{mid}

SELECT mno
FROM board_member
WHERE mid = #{mid}

SELECT mno, mid, mname, mpassword, birthday, gender, phone, zipcode, address
FROM board_member
WHERE mno = #{mno}

mapper.update:
UPDATE board_member
SET mname = #{mname}, birthday = #{birthday}, gender = #{gender},
    phone = #{phone}, zipcode = #{zipcode}, address = #{address}
WHERE mno = #{mno}
